import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuthRoutes } from "./auth-routes";
import {
  insertStoreSchema,
  insertProductSchema,
  insertCustomerSchema,
  insertTransactionSchema,
  insertDebtSchema,
  insertCashFlowEntrySchema,
} from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Setup authentication routes
  setupAuthRoutes(app);

  // Stores
  app.get("/api/stores", async (req, res) => {
    try {
      const stores = await storage.getStores();
      res.json(stores);
    } catch (error) {
      console.error("Error fetching stores:", error);
      res.status(500).json({ message: "Gagal mengambil data toko" });
    }
  });

  app.post("/api/stores", async (req, res) => {
    try {
      const storeData = insertStoreSchema.parse(req.body);
      const store = await storage.createStore(storeData);
      res.status(201).json(store);
    } catch (error) {
      console.error("Error creating store:", error);
      res.status(400).json({ message: "Data toko tidak valid" });
    }
  });

  app.get("/api/stores/:id", async (req, res) => {
    try {
      const store = await storage.getStore(req.params.id);
      if (!store) {
        return res.status(404).json({ message: "Toko tidak ditemukan" });
      }
      res.json(store);
    } catch (error) {
      console.error("Error fetching store:", error);
      res.status(500).json({ message: "Gagal mengambil data toko" });
    }
  });

  // Dashboard
  app.get("/api/stores/:id/dashboard", async (req, res) => {
    try {
      const stats = await storage.getDashboardStats(req.params.id);
      res.json(stats);
    } catch (error) {
      console.error("Error fetching dashboard stats:", error);
      res.status(500).json({ message: "Gagal mengambil statistik dashboard" });
    }
  });

  // Products
  app.get("/api/stores/:storeId/products", async (req, res) => {
    try {
      const products = await storage.getProducts(req.params.storeId);
      res.json(products);
    } catch (error) {
      console.error("Error fetching products:", error);
      res.status(500).json({ message: "Gagal mengambil data produk" });
    }
  });

  app.post("/api/stores/:storeId/products", async (req, res) => {
    try {
      const productData = insertProductSchema.parse({
        ...req.body,
        storeId: req.params.storeId,
      });
      const product = await storage.createProduct(productData);
      res.status(201).json(product);
    } catch (error) {
      console.error("Error creating product:", error);
      res.status(400).json({ message: "Data produk tidak valid" });
    }
  });

  app.put("/api/products/:id", async (req, res) => {
    try {
      const productData = insertProductSchema.partial().parse(req.body);
      const product = await storage.updateProduct(req.params.id, productData);
      res.json(product);
    } catch (error) {
      console.error("Error updating product:", error);
      res.status(400).json({ message: "Gagal memperbarui produk" });
    }
  });

  app.delete("/api/products/:id", async (req, res) => {
    try {
      await storage.deleteProduct(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting product:", error);
      res.status(500).json({ message: "Gagal menghapus produk" });
    }
  });

  // Customers
  app.get("/api/stores/:storeId/customers", async (req, res) => {
    try {
      const customers = await storage.getCustomers(req.params.storeId);
      res.json(customers);
    } catch (error) {
      console.error("Error fetching customers:", error);
      res.status(500).json({ message: "Gagal mengambil data pelanggan" });
    }
  });

  app.post("/api/stores/:storeId/customers", async (req, res) => {
    try {
      const customerData = insertCustomerSchema.parse({
        ...req.body,
        storeId: req.params.storeId,
      });
      const customer = await storage.createCustomer(customerData);
      res.status(201).json(customer);
    } catch (error) {
      console.error("Error creating customer:", error);
      res.status(400).json({ message: "Data pelanggan tidak valid" });
    }
  });

  app.put("/api/customers/:id", async (req, res) => {
    try {
      const customerData = insertCustomerSchema.partial().parse(req.body);
      const customer = await storage.updateCustomer(req.params.id, customerData);
      res.json(customer);
    } catch (error) {
      console.error("Error updating customer:", error);
      res.status(400).json({ message: "Gagal memperbarui pelanggan" });
    }
  });

  // Transactions
  app.get("/api/stores/:storeId/transactions", async (req, res) => {
    try {
      const filters: any = {};

      if (req.query.from && req.query.to) {
        filters.from = new Date(req.query.from as string);
        filters.to = new Date(req.query.to as string);
      }

      if (req.query.status) {
        filters.status = req.query.status as string;
      }

      const transactions = await storage.getTransactions(req.params.storeId, filters);
      res.json(transactions);
    } catch (error) {
      console.error("Error fetching transactions:", error);
      res.status(500).json({ message: "Gagal mengambil data transaksi" });
    }
  });

  app.post("/api/stores/:storeId/transactions", async (req, res) => {
    try {
      // Generate invoice number
      const invoiceNumber = `TRX-${Date.now()}`;

      const transactionData = insertTransactionSchema.parse({
        ...req.body,
        storeId: req.params.storeId,
        invoiceNumber,
      });

      const transaction = await storage.createTransaction(transactionData);
      res.status(201).json(transaction);
    } catch (error) {
      console.error("Error creating transaction:", error);
      res.status(400).json({ message: "Data transaksi tidak valid" });
    }
  });

  app.put("/api/transactions/:id", async (req, res) => {
    try {
      const transactionData = insertTransactionSchema.partial().parse(req.body);
      const transaction = await storage.updateTransaction(req.params.id, transactionData);
      res.json(transaction);
    } catch (error) {
      console.error("Error updating transaction:", error);
      res.status(400).json({ message: "Gagal memperbarui transaksi" });
    }
  });

  // Debts
  app.get("/api/stores/:storeId/debts", async (req, res) => {
    try {
      const debts = await storage.getDebts(req.params.storeId);
      res.json(debts);
    } catch (error) {
      console.error("Error fetching debts:", error);
      res.status(500).json({ message: "Gagal mengambil data piutang" });
    }
  });

  app.put("/api/debts/:id", async (req, res) => {
    try {
      const debtData = insertDebtSchema.partial().parse(req.body);
      const debt = await storage.updateDebt(req.params.id, debtData);
      res.json(debt);
    } catch (error) {
      console.error("Error updating debt:", error);
      res.status(400).json({ message: "Gagal memperbarui piutang" });
    }
  });

  // Mock WhatsApp reminder
  app.post("/api/debts/:id/reminder", async (req, res) => {
    try {
      const debt = await storage.getDebt(req.params.id);
      if (!debt) {
        return res.status(404).json({ message: "Piutang tidak ditemukan" });
      }

      // Mock WhatsApp sending - in production this would call WhatsApp Business API
      console.log(`Mock WhatsApp reminder sent for debt ${debt.id}`);

      await storage.updateDebt(req.params.id, {
        reminderSent: true,
        lastReminderDate: new Date(),
      });

      res.json({ message: "Reminder berhasil dikirim", success: true });
    } catch (error) {
      console.error("Error sending reminder:", error);
      res.status(500).json({ message: "Gagal mengirim reminder" });
    }
  });

  // Cash flow entries
  app.get("/api/stores/:storeId/cashflow", async (req, res) => {
    try {
      const { from, to, type } = req.query;
      const filters: any = {};

      if (from) filters.from = new Date(from as string);
      if (to) filters.to = new Date(to as string);
      if (type) filters.type = type as string;

      const entries = await storage.getCashFlowEntries(req.params.storeId, filters);
      res.json(entries);
    } catch (error) {
      console.error("Error fetching cash flow entries:", error);
      res.status(500).json({ message: "Gagal mengambil data arus kas" });
    }
  });

  app.post("/api/stores/:storeId/cashflow", async (req, res) => {
    try {
      const entryData = insertCashFlowEntrySchema.parse({
        ...req.body,
        storeId: req.params.storeId,
      });
      const entry = await storage.createCashFlowEntry(entryData);
      res.status(201).json(entry);
    } catch (error) {
      console.error("Error creating cash flow entry:", error);
      res.status(400).json({ message: "Data arus kas tidak valid" });
    }
  });

  app.put("/api/cashflow/:id", async (req, res) => {
    try {
      const entryData = insertCashFlowEntrySchema.partial().parse(req.body);
      const entry = await storage.updateCashFlowEntry(req.params.id, entryData);
      res.json(entry);
    } catch (error) {
      console.error("Error updating cash flow entry:", error);
      res.status(400).json({ message: "Data arus kas tidak valid" });
    }
  });

  app.delete("/api/cashflow/:id", async (req, res) => {
    try {
      await storage.deleteCashFlowEntry(req.params.id);
      res.json({ message: "Entry arus kas berhasil dihapus" });
    } catch (error) {
      console.error("Error deleting cash flow entry:", error);
      res.status(500).json({ message: "Gagal menghapus entry arus kas" });
    }
  });

  // Reports
  app.get("/api/stores/:storeId/reports/sales", async (req, res) => {
    try {
      const from = new Date(req.query.from as string);
      const to = new Date(req.query.to as string);

      if (isNaN(from.getTime()) || isNaN(to.getTime())) {
        return res.status(400).json({ message: "Tanggal tidak valid" });
      }

      const report = await storage.getSalesReport(req.params.storeId, from, to);
      res.json(report);
    } catch (error) {
      console.error("Error generating sales report:", error);
      res.status(500).json({ message: "Gagal membuat laporan penjualan" });
    }
  });

  // Mock payment callback
  app.post("/api/payment/callback", async (req, res) => {
    try {
      const { transactionId, status } = req.body;

      if (status === "paid") {
        await storage.updateTransaction(transactionId, {
          paymentStatus: "paid",
        });

        console.log(`Mock payment callback: Transaction ${transactionId} marked as paid`);
      }

      res.json({ success: true });
    } catch (error) {
      console.error("Error processing payment callback:", error);
      res.status(500).json({ message: "Gagal memproses callback pembayaran" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  stores,
  products,
  customers,
  transactions,
  debts,
  stockMovements,
  cashFlowEntries,
  auditLogs,
  type Store,
  type InsertStore,
  type Product,
  type InsertProduct,
  type Customer,
  type InsertCustomer,
  type Transaction,
  type InsertTransaction,
  type Debt,
  type InsertDebt,
  type StockMovement,
  type InsertStockMovement,
  type CashFlowEntry,
  type InsertCashFlowEntry,
  type AuditLog,
  type InsertAuditLog,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, sql, and, gte, lte, sum, count, isNull } from "drizzle-orm";

export interface IStorage {
  // Stores
  getStores(): Promise<Store[]>;
  getStore(id: string): Promise<Store | undefined>;
  createStore(store: InsertStore): Promise<Store>;
  updateStore(id: string, store: Partial<InsertStore>): Promise<Store>;

  // Products
  getProducts(storeId: string): Promise<Product[]>;
  getProduct(id: string): Promise<Product | undefined>;
  createProduct(product: InsertProduct): Promise<Product>;
  updateProduct(id: string, product: Partial<InsertProduct>): Promise<Product>;
  deleteProduct(id: string): Promise<void>;
  getLowStockProducts(storeId: string, threshold?: number): Promise<Product[]>;

  // Customers
  getCustomers(storeId: string): Promise<Customer[]>;
  getCustomer(id: string): Promise<Customer | undefined>;
  createCustomer(customer: InsertCustomer): Promise<Customer>;
  updateCustomer(id: string, customer: Partial<InsertCustomer>): Promise<Customer>;
  deleteCustomer(id: string): Promise<void>;

  // Transactions
  getTransactions(storeId: string, filters?: { from?: Date; to?: Date; status?: string }): Promise<Transaction[]>;
  getTransaction(id: string): Promise<Transaction | undefined>;
  createTransaction(transaction: InsertTransaction): Promise<Transaction>;
  updateTransaction(id: string, transaction: Partial<InsertTransaction>): Promise<Transaction>;

  // Debts
  getDebts(storeId: string): Promise<Debt[]>;
  getDebt(id: string): Promise<Debt | undefined>;
  createDebt(debt: InsertDebt): Promise<Debt>;
  updateDebt(id: string, debt: Partial<InsertDebt>): Promise<Debt>;
  getCustomerDebts(customerId: string): Promise<Debt[]>;

  // Stock movements
  createStockMovement(movement: InsertStockMovement): Promise<StockMovement>;
  getStockMovements(productId: string): Promise<StockMovement[]>;

  // Cash flow entries
  getCashFlowEntries(storeId: string, filters?: { from?: Date; to?: Date; type?: string }): Promise<CashFlowEntry[]>;
  createCashFlowEntry(entry: InsertCashFlowEntry): Promise<CashFlowEntry>;
  updateCashFlowEntry(id: string, entry: Partial<InsertCashFlowEntry>): Promise<CashFlowEntry>;
  deleteCashFlowEntry(id: string): Promise<void>;

  // Dashboard stats
  getDashboardStats(storeId: string): Promise<{
    dailySales: number;
    transactionCount: number;
    totalDebt: number;
    lowStockCount: number;
    recentTransactions: Transaction[];
  }>;

  // Reports
  getSalesReport(storeId: string, from: Date, to: Date): Promise<{
    totalRevenue: number;
    totalTransactions: number;
    grossProfit: number;
    averageTransaction: number;
    topProducts: Array<{
      productName: string;
      quantity: number;
      revenue: number;
    }>;
    paymentMethods: Array<{
      method: string;
      amount: number;
      count: number;
    }>;
  }>;

  // Audit logs
  createAuditLog(log: InsertAuditLog): Promise<AuditLog>;
}

export class DatabaseStorage implements IStorage {
  async getStores(): Promise<Store[]> {
    return await db.select().from(stores).orderBy(stores.name);
  }

  async getStore(id: string): Promise<Store | undefined> {
    const [store] = await db.select().from(stores).where(eq(stores.id, id));
    return store;
  }

  async createStore(store: InsertStore): Promise<Store> {
    const [created] = await db.insert(stores).values(store).returning();
    return created;
  }

  async updateStore(id: string, store: Partial<InsertStore>): Promise<Store> {
    const [updated] = await db
      .update(stores)
      .set({ ...store, updatedAt: new Date() })
      .where(eq(stores.id, id))
      .returning();
    return updated;
  }

  async getProducts(storeId: string): Promise<Product[]> {
    return await db
      .select()
      .from(products)
      .where(and(eq(products.storeId, storeId), eq(products.isActive, true)))
      .orderBy(products.name);
  }

  async getProduct(id: string): Promise<Product | undefined> {
    const [product] = await db.select().from(products).where(eq(products.id, id));
    return product;
  }

  async createProduct(product: InsertProduct): Promise<Product> {
    const [created] = await db.insert(products).values(product).returning();
    return created;
  }

  async updateProduct(id: string, product: Partial<InsertProduct>): Promise<Product> {
    const [updated] = await db
      .update(products)
      .set({ ...product, updatedAt: new Date() })
      .where(eq(products.id, id))
      .returning();
    return updated;
  }

  async deleteProduct(id: string): Promise<void> {
    await db.update(products).set({ isActive: false }).where(eq(products.id, id));
  }

  async getLowStockProducts(storeId: string, threshold = 5): Promise<Product[]> {
    return await db
      .select()
      .from(products)
      .where(
        and(
          eq(products.storeId, storeId),
          eq(products.isActive, true),
          sql`${products.stock} <= ${threshold}`
        )
      )
      .orderBy(products.stock);
  }

  async getCustomers(storeId: string): Promise<Customer[]> {
    return await db
      .select()
      .from(customers)
      .where(eq(customers.storeId, storeId))
      .orderBy(customers.name);
  }

  async getCustomer(id: string): Promise<Customer | undefined> {
    const [customer] = await db.select().from(customers).where(eq(customers.id, id));
    return customer;
  }

  async createCustomer(customer: InsertCustomer): Promise<Customer> {
    const [created] = await db.insert(customers).values(customer).returning();
    return created;
  }

  async updateCustomer(id: string, customer: Partial<InsertCustomer>): Promise<Customer> {
    const [updated] = await db
      .update(customers)
      .set({ ...customer, updatedAt: new Date() })
      .where(eq(customers.id, id))
      .returning();
    return updated;
  }

  async deleteCustomer(id: string): Promise<void> {
    await db.delete(customers).where(eq(customers.id, id));
  }

  async getTransactions(
    storeId: string,
    filters?: { from?: Date; to?: Date; status?: string }
  ): Promise<Transaction[]> {
    let whereConditions = [eq(transactions.storeId, storeId)];

    if (filters?.from && filters?.to) {
      whereConditions.push(gte(transactions.createdAt, filters.from));
      whereConditions.push(lte(transactions.createdAt, filters.to));
    }

    if (filters?.status) {
      whereConditions.push(eq(transactions.paymentStatus, filters.status));
    }

    return await db
      .select()
      .from(transactions)
      .where(and(...whereConditions))
      .orderBy(desc(transactions.createdAt));
  }

  async getTransaction(id: string): Promise<Transaction | undefined> {
    const [transaction] = await db.select().from(transactions).where(eq(transactions.id, id));
    return transaction;
  }

  async createTransaction(transaction: InsertTransaction): Promise<Transaction> {
    return await db.transaction(async (tx) => {
      // Create transaction
      const [created] = await tx.insert(transactions).values({
        ...transaction,
        items: transaction.items as Array<{
          productId: string;
          productName: string;
          quantity: number;
          price: number;
          discount: number;
        }>
      }).returning();

      // Update product stock
      for (const item of transaction.items) {
        await tx
          .update(products)
          .set({
            stock: sql`${products.stock} - ${item.quantity}`,
            updatedAt: new Date(),
          })
          .where(eq(products.id, item.productId));

        // Create stock movement
        await tx.insert(stockMovements).values({
          productId: item.productId,
          storeId: transaction.storeId,
          type: "out",
          quantity: -item.quantity,
          reference: created.id,
          notes: "Sale transaction",
        });
      }

      // Create debt if unpaid
      if (
        created.paymentStatus === "unpaid" &&
        created.customerId &&
        parseFloat(created.total) > 0
      ) {
        await tx.insert(debts).values({
          transactionId: created.id,
          storeId: created.storeId,
          customerId: created.customerId,
          amount: created.total,
          status: "pending",
        });
      }

      return created;
    });
  }

  async updateTransaction(id: string, transaction: Partial<InsertTransaction>): Promise<Transaction> {
    const updateData: any = { ...transaction, updatedAt: new Date() };
    if (transaction.items) {
      updateData.items = transaction.items;
    }

    const [updated] = await db
      .update(transactions)
      .set(updateData)
      .where(eq(transactions.id, id))
      .returning();
    return updated;
  }

  async getDebts(storeId: string): Promise<Debt[]> {
    return await db
      .select()
      .from(debts)
      .where(eq(debts.storeId, storeId))
      .orderBy(desc(debts.createdAt));
  }

  async getDebt(id: string): Promise<Debt | undefined> {
    const [debt] = await db.select().from(debts).where(eq(debts.id, id));
    return debt;
  }

  async createDebt(debt: InsertDebt): Promise<Debt> {
    const [created] = await db.insert(debts).values(debt).returning();
    return created;
  }

  async updateDebt(id: string, debt: Partial<InsertDebt>): Promise<Debt> {
    const [updated] = await db
      .update(debts)
      .set({ ...debt, updatedAt: new Date() })
      .where(eq(debts.id, id))
      .returning();
    return updated;
  }

  async getCustomerDebts(customerId: string): Promise<Debt[]> {
    return await db
      .select()
      .from(debts)
      .where(eq(debts.customerId, customerId))
      .orderBy(desc(debts.createdAt));
  }

  async createStockMovement(movement: InsertStockMovement): Promise<StockMovement> {
    const [created] = await db.insert(stockMovements).values(movement).returning();
    return created;
  }

  async getStockMovements(productId: string): Promise<StockMovement[]> {
    return await db
      .select()
      .from(stockMovements)
      .where(eq(stockMovements.productId, productId))
      .orderBy(desc(stockMovements.createdAt));
  }

  async getCashFlowEntries(storeId: string, filters?: { from?: Date; to?: Date; type?: string }): Promise<CashFlowEntry[]> {
    let whereConditions = [eq(cashFlowEntries.storeId, storeId)];

    if (filters?.from && filters?.to) {
      whereConditions.push(gte(cashFlowEntries.createdAt, filters.from));
      whereConditions.push(lte(cashFlowEntries.createdAt, filters.to));
    }

    if (filters?.type) {
      whereConditions.push(eq(cashFlowEntries.type, filters.type));
    }

    return await db
      .select()
      .from(cashFlowEntries)
      .where(and(...whereConditions))
      .orderBy(desc(cashFlowEntries.createdAt));
  }

  async createCashFlowEntry(entry: InsertCashFlowEntry): Promise<CashFlowEntry> {
    const [created] = await db.insert(cashFlowEntries).values(entry).returning();
    return created;
  }

  async updateCashFlowEntry(id: string, entry: Partial<InsertCashFlowEntry>): Promise<CashFlowEntry> {
    const [updated] = await db
      .update(cashFlowEntries)
      .set({ ...entry, updatedAt: new Date() })
      .where(eq(cashFlowEntries.id, id))
      .returning();
    return updated;
  }

  async deleteCashFlowEntry(id: string): Promise<void> {
    await db.delete(cashFlowEntries).where(eq(cashFlowEntries.id, id));
  }

  async getDashboardStats(storeId: string) {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);

    // Daily sales
    const [dailySalesResult] = await db
      .select({ total: sum(transactions.total) })
      .from(transactions)
      .where(
        and(
          eq(transactions.storeId, storeId),
          eq(transactions.paymentStatus, "paid"),
          gte(transactions.createdAt, today),
          lte(transactions.createdAt, tomorrow)
        )
      );

    // Transaction count
    const [transactionCountResult] = await db
      .select({ count: count() })
      .from(transactions)
      .where(
        and(
          eq(transactions.storeId, storeId),
          gte(transactions.createdAt, today),
          lte(transactions.createdAt, tomorrow)
        )
      );

    // Total debt
    const [totalDebtResult] = await db
      .select({ total: sum(sql`${debts.amount} - ${debts.paidAmount}`) })
      .from(debts)
      .where(and(eq(debts.storeId, storeId), eq(debts.status, "pending")));

    // Low stock count
    const lowStockProducts = await this.getLowStockProducts(storeId);

    // Recent transactions
    const recentTransactions = await db
      .select()
      .from(transactions)
      .where(eq(transactions.storeId, storeId))
      .orderBy(desc(transactions.createdAt))
      .limit(5);

    return {
      dailySales: parseFloat(dailySalesResult?.total || "0"),
      transactionCount: transactionCountResult?.count || 0,
      totalDebt: parseFloat(totalDebtResult?.total || "0"),
      lowStockCount: lowStockProducts.length,
      recentTransactions,
    };
  }

  async getSalesReport(storeId: string, from: Date, to: Date) {
    // Total revenue
    const [revenueResult] = await db
      .select({ total: sum(transactions.total) })
      .from(transactions)
      .where(
        and(
          eq(transactions.storeId, storeId),
          eq(transactions.paymentStatus, "paid"),
          gte(transactions.createdAt, from),
          lte(transactions.createdAt, to)
        )
      );

    // Total transactions
    const [transactionCountResult] = await db
      .select({ count: count() })
      .from(transactions)
      .where(
        and(
          eq(transactions.storeId, storeId),
          gte(transactions.createdAt, from),
          lte(transactions.createdAt, to)
        )
      );

    const totalRevenue = parseFloat(revenueResult?.total || "0");
    const totalTransactions = transactionCountResult?.count || 0;

    return {
      totalRevenue,
      totalTransactions,
      grossProfit: totalRevenue * 0.27, // Estimated margin
      averageTransaction: totalTransactions > 0 ? totalRevenue / totalTransactions : 0,
      topProducts: [], // Would need complex aggregation
      paymentMethods: [], // Would need complex aggregation
    };
  }

  async createAuditLog(log: InsertAuditLog): Promise<AuditLog> {
    const [created] = await db.insert(auditLogs).values(log).returning();
    return created;
  }
}

export const storage = new DatabaseStorage();

